function verifyCaptcha(userSelection) {
    const chaotic = userSelection.filter(shape => 
        Math.abs(shape.chaosLevel) > 0.5 // Extremos do espectro
    );
    return chaotic.length >= 3; // Sucesso se selecionou caos
}

